mapper.select:
SELECT stu_id, stu_name, pwd, role, clazz_name
FROM student
WHERE stu_id = #{stuId}
  AND pwd = #{pwd}
  AND role IN ('admin', 'sa')

SELECT stu_id, stu_name, clazz_name
FROM student
WHERE clazz_name = #{clazzName}

mapper.update:
UPDATE student
SET pwd = #{pwd}
WHERE stu_id = #{stuId}
