mapper.select:
SELECT user_id, username, pwd, role, organize_name
FROM user
WHERE user_id = #{userId}
  AND pwd = #{pwd}
  AND role IN ('admin', 'sa')

SELECT user_id, username, organize_name
FROM user
WHERE organize_name = #{organizeName}

mapper.update:
UPDATE user
SET pwd = #{pwd}
WHERE user_id = #{userId}
