mapper.select:
SELECT user_id, username, organize_name FROM user,organize WHERE user.organize_id = organize.id
AND user_id = #{userId}
AND username LIKE CONCAT('%',#{username},'%')

mapper.update:
UPDATE user
SET pwd = #{pwd}
WHERE user_id = #{userId}

UPDATE user
SET user_id  = ${userId},
    username = #{username}
WHERE user_id = #{oldId}
